<input type="textbox" class="form-control custom-fields @if($field->field_validation == \App\Field::VALIDATION_FROM_LIST){{ 'form-list-field' }}@endif" name="field-{{ $type }}-{{ $field->id }}" field-id="{{ $field->id }}" @if($field->default_field_type == 'first_name' || $field->default_field_type == 'last_name')style="text-transform: capitalize;" @endif autocomplete="offf" autocorrect="off" spellcheck="false"  @if($field->field_validation == \App\Field::VALIDATION_DATE) placeholder="mm/dd/yyyy" @endif @if(!empty($value)) value="{{ $value }}" @endif @if($field->default_field_type) default-field-type="{{ $field->default_field_type }}" @endif @if($field->field_validation == \App\Field::VALIDATION_FROM_LIST)field-list-fields-options="{{ $field->options }}"@endif>